import apiClient from './authService';

export interface TeamMember {
  id: string;
  email: string;
  name: string;
  role: 'USER' | 'ADMIN' | 'MANAGER' | 'HR_SPECIALIST' | 'SUPER_ADMIN';
  isActive: boolean;
  isOnboarded: boolean;
  createdAt: string;
  updatedAt: string;
}

export interface TeamListResponse {
  success: boolean;
  data: {
    users: TeamMember[];
    pagination: {
      page: number;
      limit: number;
      total: number;
      pages: number;
    };
  };
}

export interface InviteRequest {
  email: string;
  role: string;
  name?: string;
}

export interface UpdateRequest {
  role?: string;
  name?: string;
  isActive?: boolean;
}

/**
 * Get team members list
 */
export async function getTeamMembers(
  page: number = 1,
  limit: number = 10,
  search: string = '',
  role: string = ''
): Promise<TeamListResponse> {
  const params = new URLSearchParams();
  params.append('page', page.toString());
  params.append('limit', limit.toString());
  if (search) params.append('search', search);
  if (role) params.append('role', role);

  const response = await apiClient.get<TeamListResponse>(
    `/api/v1/team?${params.toString()}`
  );
  return response.data;
}

/**
 * Get single team member
 */
export async function getTeamMember(id: string): Promise<TeamMember> {
  const response = await apiClient.get<{ success: boolean; data: TeamMember }>(
    `/api/v1/team/${id}`
  );
  return response.data.data;
}

/**
 * Invite team member
 */
export async function inviteTeamMember(data: InviteRequest): Promise<TeamMember> {
  const response = await apiClient.post<{ success: boolean; data: TeamMember }>(
    '/api/v1/team/invite',
    data
  );
  return response.data.data;
}

/**
 * Update team member
 */
export async function updateTeamMember(id: string, data: UpdateRequest): Promise<TeamMember> {
  const response = await apiClient.patch<{ success: boolean; data: TeamMember }>(
    `/api/v1/team/${id}`,
    data
  );
  return response.data.data;
}

/**
 * Toggle team member active status
 */
export async function toggleTeamMember(id: string): Promise<TeamMember> {
  const response = await apiClient.patch<{ success: boolean; data: TeamMember }>(
    `/api/v1/team/${id}/toggle`
  );
  return response.data.data;
}

/**
 * Delete team member
 */
export async function deleteTeamMember(id: string): Promise<void> {
  await apiClient.delete(`/api/v1/team/${id}`);
}

'use client';

import { useState, useEffect } from 'react';
import { Users, UserPlus, Search, Filter, Edit, Trash2, CheckCircle, XCircle } from 'lucide-react';
import { useAuthStore } from '@/lib/store/authStore';
import { getTeamMembers, toggleTeamMember, deleteTeamMember, TeamMember } from '@/lib/services/teamService';
import { useToast } from '@/lib/hooks/useToast';
import InviteUserModal from '@/components/team/InviteUserModal';
import EditUserModal from '@/components/team/EditUserModal';
import ConfirmDialog from '@/components/ui/ConfirmDialog';
import LoadingSkeleton from '@/components/ui/LoadingSkeleton';

export default function TeamManagementPage() {
  const { user } = useAuthStore();
  const toast = useToast();

  const [members, setMembers] = useState<TeamMember[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [search, setSearch] = useState('');
  const [roleFilter, setRoleFilter] = useState('');

  const [inviteModalOpen, setInviteModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedMember, setSelectedMember] = useState<TeamMember | null>(null);

  // Check if user has admin access
  const hasAccess = user?.role === 'ADMIN' || user?.role === 'SUPER_ADMIN';

  // Fetch team members
  const fetchMembers = async () => {
    setLoading(true);
    try {
      const response = await getTeamMembers(page, 10, search, roleFilter);
      setMembers(response.data.users);
      setTotalPages(response.data.pagination.pages);
    } catch (error: any) {
      toast.error(error.message || 'Takım üyeleri yüklenemedi');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (hasAccess) {
      fetchMembers();
    }
  }, [page, search, roleFilter, hasAccess]);

  // Handle toggle active
  const handleToggle = async (member: TeamMember) => {
    try {
      await toggleTeamMember(member.id);
      toast.success(`${member.name} ${!member.isActive ? 'aktif' : 'pasif'} edildi`);
      fetchMembers();
    } catch (error: any) {
      toast.error(error.message || 'Durum değiştirilemedi');
    }
  };

  // Handle delete
  const handleDelete = async () => {
    if (!selectedMember) return;
    try {
      await deleteTeamMember(selectedMember.id);
      toast.success('Kullanıcı silindi');
      setDeleteDialogOpen(false);
      fetchMembers();
    } catch (error: any) {
      toast.error(error.message || 'Kullanıcı silinemedi');
    }
  };

  // Access denied
  if (!hasAccess) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-8 text-center">
          <XCircle size={48} className="text-red-600 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Erişim Engellendi</h2>
          <p className="text-gray-600">Bu sayfaya erişmek için ADMIN yetkisi gerekiyor.</p>
        </div>
      </div>
    );
  }

  const roleLabels: Record<string, string> = {
    ADMIN: 'Yönetici',
    MANAGER: 'Müdür',
    HR_SPECIALIST: 'İK Uzmanı',
    USER: 'Kullanıcı',
    SUPER_ADMIN: 'Süper Admin'
  };

  const roleBadgeColors: Record<string, string> = {
    ADMIN: 'bg-purple-100 text-purple-800',
    MANAGER: 'bg-blue-100 text-blue-800',
    HR_SPECIALIST: 'bg-green-100 text-green-800',
    USER: 'bg-gray-100 text-gray-800',
    SUPER_ADMIN: 'bg-red-100 text-red-800'
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-3">
                <Users size={32} />
                Takım Yönetimi
              </h1>
              <p className="text-gray-600 mt-1">Organizasyon üyelerini yönetin</p>
            </div>
            <button
              onClick={() => setInviteModalOpen(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
            >
              <UserPlus size={20} />
              Yeni Kullanıcı Davet Et
            </button>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-sm p-4 mb-6">
          <div className="flex gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="İsim veya email ile ara..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            <select
              value={roleFilter}
              onChange={(e) => setRoleFilter(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Tüm Roller</option>
              <option value="ADMIN">Yönetici</option>
              <option value="MANAGER">Müdür</option>
              <option value="HR_SPECIALIST">İK Uzmanı</option>
              <option value="USER">Kullanıcı</option>
            </select>
          </div>
        </div>

        {/* Table */}
        {loading ? (
          <LoadingSkeleton variant="table" rows={5} />
        ) : (
          <div className="bg-white rounded-lg shadow-sm overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Kullanıcı</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Rol</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Durum</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Kayıt Tarihi</th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">İşlemler</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {members.map((member) => (
                  <tr key={member.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">{member.name}</div>
                        <div className="text-sm text-gray-500">{member.email}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 py-1 text-xs font-semibold rounded-full ${roleBadgeColors[member.role]}`}>
                        {roleLabels[member.role]}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {member.isActive ? (
                        <span className="px-2 py-1 text-xs font-semibold rounded-full bg-green-100 text-green-800">
                          Aktif
                        </span>
                      ) : (
                        <span className="px-2 py-1 text-xs font-semibold rounded-full bg-gray-100 text-gray-800">
                          Pasif
                        </span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {new Date(member.createdAt).toLocaleDateString('tr-TR')}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button
                        onClick={() => { setSelectedMember(member); setEditModalOpen(true); }}
                        className="text-blue-600 hover:text-blue-900 mr-3"
                        disabled={member.role === 'SUPER_ADMIN'}
                      >
                        <Edit size={18} />
                      </button>
                      <button
                        onClick={() => handleToggle(member)}
                        className="text-yellow-600 hover:text-yellow-900 mr-3"
                        disabled={member.role === 'SUPER_ADMIN' || member.id === user?.id}
                      >
                        {member.isActive ? <XCircle size={18} /> : <CheckCircle size={18} />}
                      </button>
                      <button
                        onClick={() => { setSelectedMember(member); setDeleteDialogOpen(true); }}
                        className="text-red-600 hover:text-red-900"
                        disabled={member.role === 'SUPER_ADMIN' || member.id === user?.id}
                      >
                        <Trash2 size={18} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="px-6 py-4 flex items-center justify-between border-t border-gray-200">
                <button
                  onClick={() => setPage(p => Math.max(1, p - 1))}
                  disabled={page === 1}
                  className="px-4 py-2 border border-gray-300 rounded-lg disabled:opacity-50"
                >
                  Önceki
                </button>
                <span className="text-sm text-gray-700">
                  Sayfa {page} / {totalPages}
                </span>
                <button
                  onClick={() => setPage(p => Math.min(totalPages, p + 1))}
                  disabled={page === totalPages}
                  className="px-4 py-2 border border-gray-300 rounded-lg disabled:opacity-50"
                >
                  Sonraki
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Modals */}
      <InviteUserModal
        isOpen={inviteModalOpen}
        onClose={() => setInviteModalOpen(false)}
        onSuccess={fetchMembers}
      />

      <EditUserModal
        isOpen={editModalOpen}
        onClose={() => { setEditModalOpen(false); setSelectedMember(null); }}
        member={selectedMember}
        onSuccess={fetchMembers}
      />

      <ConfirmDialog
        isOpen={deleteDialogOpen}
        onClose={() => { setDeleteDialogOpen(false); setSelectedMember(null); }}
        onConfirm={handleDelete}
        title="Kullanıcıyı Sil"
        message={`${selectedMember?.name} adlı kullanıcıyı silmek istediğinize emin misiniz?`}
        confirmText="Sil"
        confirmButtonClass="bg-red-600 hover:bg-red-700"
      />
    </div>
  );
}

'use client';

import { useState } from 'react';
import { X, Mail, UserPlus } from 'lucide-react';
import { inviteTeamMember } from '@/lib/services/teamService';
import { useToast } from '@/lib/hooks/useToast';

interface InviteUserModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
}

export default function InviteUserModal({ isOpen, onClose, onSuccess }: InviteUserModalProps) {
  const toast = useToast();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    name: '',
    role: 'USER'
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      await inviteTeamMember({
        email: formData.email,
        name: formData.name || undefined,
        role: formData.role
      });
      toast.success('Davet başarıyla gönderildi!');
      onClose();
      onSuccess();
      setFormData({ email: '', name: '', role: 'USER' });
    } catch (error: any) {
      toast.error(error.response?.data?.message || 'Davet gönderilemedi');
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <UserPlus size={24} className="text-blue-600" />
            <h2 className="text-xl font-bold text-gray-900">Yeni Kullanıcı Davet Et</h2>
          </div>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={24} />
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email Adresi *
            </label>
            <input
              type="email"
              required
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="kullanici@example.com"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              İsim (Opsiyonel)
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Ahmet Yılmaz"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Rol *
            </label>
            <select
              required
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="USER">Kullanıcı</option>
              <option value="HR_SPECIALIST">İK Uzmanı</option>
              <option value="MANAGER">Müdür</option>
              <option value="ADMIN">Yönetici</option>
            </select>
          </div>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
            <p className="text-sm text-blue-800">
              <Mail size={16} className="inline mr-2" />
              Davet linki email ile gönderilecektir. Kullanıcı 7 gün içinde daveti kabul edebilir.
            </p>
          </div>

          {/* Actions */}
          <div className="flex gap-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
              disabled={loading}
            >
              İptal
            </button>
            <button
              type="submit"
              disabled={loading}
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
            >
              {loading ? 'Gönderiliyor...' : 'Davet Gönder'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { X, Edit } from 'lucide-react';
import { updateTeamMember, TeamMember } from '@/lib/services/teamService';
import { useToast } from '@/lib/hooks/useToast';

interface EditUserModalProps {
  isOpen: boolean;
  onClose: () => void;
  member: TeamMember | null;
  onSuccess: () => void;
}

export default function EditUserModal({ isOpen, onClose, member, onSuccess }: EditUserModalProps) {
  const toast = useToast();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    role: 'USER'
  });

  useEffect(() => {
    if (member) {
      setFormData({
        name: member.name,
        role: member.role
      });
    }
  }, [member]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!member) return;

    setLoading(true);
    try {
      await updateTeamMember(member.id, formData);
      toast.success('Kullanıcı güncellendi');
      onClose();
      onSuccess();
    } catch (error: any) {
      toast.error(error.response?.data?.message || 'Güncellenemedi');
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen || !member) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <Edit size={24} className="text-blue-600" />
            <h2 className="text-xl font-bold text-gray-900">Kullanıcı Düzenle</h2>
          </div>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
            <input
              type="email"
              value={member.email}
              disabled
              className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">İsim *</label>
            <input
              type="text"
              required
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Rol *</label>
            <select
              required
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            >
              <option value="USER">Kullanıcı</option>
              <option value="HR_SPECIALIST">İK Uzmanı</option>
              <option value="MANAGER">Müdür</option>
              <option value="ADMIN">Yönetici</option>
            </select>
          </div>

          <div className="flex gap-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              İptal
            </button>
            <button
              type="submit"
              disabled={loading}
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
            >
              {loading ? 'Kaydediliyor...' : 'Kaydet'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

'use client';

import { AlertTriangle, X } from 'lucide-react';

interface ConfirmDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title: string;
  message: string;
  confirmText?: string;
  cancelText?: string;
  confirmButtonClass?: string;
}

export default function ConfirmDialog({
  isOpen,
  onClose,
  onConfirm,
  title,
  message,
  confirmText = 'Onayla',
  cancelText = 'İptal',
  confirmButtonClass = 'bg-red-600 hover:bg-red-700'
}: ConfirmDialogProps) {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <AlertTriangle size={24} className="text-yellow-600" />
            <h2 className="text-xl font-bold text-gray-900">{title}</h2>
          </div>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={24} />
          </button>
        </div>

        <p className="text-gray-600 mb-6">{message}</p>

        <div className="flex gap-3">
          <button
            onClick={onClose}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
          >
            {cancelText}
          </button>
          <button
            onClick={onConfirm}
            className={`flex-1 px-4 py-2 text-white rounded-lg ${confirmButtonClass}`}
          >
            {confirmText}
          </button>
        </div>
      </div>
    </div>
  );
}
